mapper.select:
select * from order_master
where shop_id=#{shopId}
order by create_time desc
limit #{offset},#{pageSize}

select order_status
from order_master
where order_number=#{orderNumber}

mapper.update:
update order_master
set order_status=3
where order_number=#{orderNumber}
and shop_id=#{shopId}

update order_master
set order_status=2
where order_number=#{orderNumber}
and shop_id=#{shopId}
