mapper.select:
SELECT * FROM order_master
WHERE shop_id=#{shopId}
limit #{offset},#{pageSize}

select order_status
from order_master
where order_number=#{orderNumber}

mapper.update:
update order_master
set order_status=3
where order_number=#{orderNumber}

update order_master
set order_status=2
where order_number=#{orderNumber}
