mapper.update:
update user
username=#{username}, telephone=#{telephone}, address=#{address}, sex=#{sex}, age=#{age}, real_name=#{realName}, email=#{email}, avatar=#{avatar}
where user_id=#{userId}

mapper.select:
select * from user
where concat('user_id','username','real_name','role','age','sex','address','telephone','status')
          like concat('%'#{key}'%') limit #{pageNum},#{pageSize}
